contracts/YieldForge/YieldForgeToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title YieldForgeToken ($YFORGE)
 * @notice Governance and utility token for YieldForge platform
 * @dev ERC20 token with burning capability for deflationary tokenomics
 */
contract YieldForgeToken is ERC20, ERC20Burnable, Ownable {
    
    // Token distribution
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    uint256 public constant TEAM_ALLOCATION = 200_000_000 * 10**18; // 20%
    uint256 public constant COMMUNITY_ALLOCATION = 300_000_000 * 10**18; // 30%
    uint256 public constant LIQUIDITY_ALLOCATION = 200_000_000 * 10**18; // 20%
    uint256 public constant TREASURY_ALLOCATION = 300_000_000 * 10**18; // 30%

    // Vesting
    mapping(address => VestingSchedule) public vestingSchedules;
    
    struct VestingSchedule {
        uint256 totalAmount;
        uint256 releasedAmount;
        uint256 startTime;
        uint256 duration;
    }

    // Staking
    mapping(address => StakeInfo) public stakes;
    uint256 public totalStaked;
    uint256 public rewardRate = 1000; // 10% APY in basis points

    struct StakeInfo {
        uint256 amount;
        uint256 startTime;
        uint256 lastClaimTime;
    }

    // Events
    event TokensStaked(address indexed user, uint256 amount);
    event TokensUnstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event VestingScheduleCreated(address indexed beneficiary, uint256 amount, uint256 duration);

    constructor() ERC20("YieldForge Token", "YFORGE") Ownable(msg.sender) {
        // Mint total supply to contract deployer
        _mint(msg.sender, TOTAL_SUPPLY);
    }

    /**
     * @notice Stake tokens to earn rewards
     * @param amount Amount of tokens to stake
     */
    function stake(uint256 amount) external {
        require(amount > 0, "Amount must be > 0");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");

        // Claim pending rewards first
        if (stakes[msg.sender].amount > 0) {
            _claimRewards(msg.sender);
        }

        // Transfer tokens to contract
        _transfer(msg.sender, address(this), amount);

        // Update stake info
        stakes[msg.sender].amount += amount;
        stakes[msg.sender].startTime = block.timestamp;
        stakes[msg.sender].lastClaimTime = block.timestamp;
        totalStaked += amount;

        emit TokensStaked(msg.sender, amount);
    }

    /**
     * @notice Unstake tokens
     * @param amount Amount of tokens to unstake
     */
    function unstake(uint256 amount) external {
        require(amount > 0, "Amount must be > 0");
        require(stakes[msg.sender].amount >= amount, "Insufficient staked");

        // Claim pending rewards first
        _claimRewards(msg.sender);

        // Update stake info
        stakes[msg.sender].amount -= amount;
        totalStaked -= amount;

        // Transfer tokens back to user
        _transfer(address(this), msg.sender, amount);

        emit TokensUnstaked(msg.sender, amount);
    }

    /**
     * @notice Claim staking rewards
     */
    function claimRewards() external {
        require(stakes[msg.sender].amount > 0, "No stake");
        _claimRewards(msg.sender);
    }

    /**
     * @notice Internal function to claim rewards
     */
    function _claimRewards(address user) internal {
        StakeInfo storage stakeInfo = stakes[user];
        
        uint256 timeStaked = block.timestamp - stakeInfo.lastClaimTime;
        uint256 rewards = (stakeInfo.amount * timeStaked * rewardRate) / (365 days * 10000);

        if (rewards > 0) {
            stakeInfo.lastClaimTime = block.timestamp;
            _mint(user, rewards); // Mint new tokens as rewards
            emit RewardsClaimed(user, rewards);
        }
    }

    /**
     * @notice Create vesting schedule for team/advisors
     * @param beneficiary Address of the beneficiary
     * @param amount Total amount to vest
     * @param duration Vesting duration in seconds
     */
    function createVestingSchedule(
        address beneficiary,
        uint256 amount,
        uint256 duration
    ) external onlyOwner {
        require(beneficiary != address(0), "Invalid beneficiary");
        require(amount > 0, "Amount must be > 0");
        require(duration > 0, "Duration must be > 0");
        require(vestingSchedules[beneficiary].totalAmount == 0, "Schedule exists");

        vestingSchedules[beneficiary] = VestingSchedule({
            totalAmount: amount,
            releasedAmount: 0,
            startTime: block.timestamp,
            duration: duration
        });

        emit VestingScheduleCreated(beneficiary, amount, duration);
    }

    /**
     * @notice Release vested tokens
     */
    function releaseVestedTokens() external {
        VestingSchedule storage schedule = vestingSchedules[msg.sender];
        require(schedule.totalAmount > 0, "No vesting schedule");

        uint256 vestedAmount = _calculateVestedAmount(msg.sender);
        uint256 releasable = vestedAmount - schedule.releasedAmount;
        
        require(releasable > 0, "No tokens to release");

        schedule.releasedAmount += releasable;
        _transfer(owner(), msg.sender, releasable);
    }

    /**
     * @notice Calculate vested amount
     */
    function _calculateVestedAmount(address beneficiary) internal view returns (uint256) {
        VestingSchedule memory schedule = vestingSchedules[beneficiary];
        
        if (block.timestamp < schedule.startTime) {
            return 0;
        }

        uint256 timeElapsed = block.timestamp - schedule.startTime;
        
        if (timeElapsed >= schedule.duration) {
            return schedule.totalAmount;
        }

        return (schedule.totalAmount * timeElapsed) / schedule.duration;
    }

    /**
     * @notice Get pending rewards for a user
     */
    function getPendingRewards(address user) external view returns (uint256) {
        StakeInfo memory stakeInfo = stakes[user];
        if (stakeInfo.amount == 0) return 0;

        uint256 timeStaked = block.timestamp - stakeInfo.lastClaimTime;
        return (stakeInfo.amount * timeStaked * rewardRate) / (365 days * 10000);
    }

    /**
     * @notice Get stake info for a user
     */
    function getStakeInfo(address user) external view returns (
        uint256 amount,
        uint256 startTime,
        uint256 pendingRewards
    ) {
        StakeInfo memory stakeInfo = stakes[user];
        uint256 timeStaked = block.timestamp - stakeInfo.lastClaimTime;
        uint256 pending = (stakeInfo.amount * timeStaked * rewardRate) / (365 days * 10000);
        
        return (stakeInfo.amount, stakeInfo.startTime, pending);
    }

    /**
     * @notice Update reward rate (only owner)
     */
    function setRewardRate(uint256 newRate) external onlyOwner {
        require(newRate <= 5000, "Rate too high"); // Max 50% APY
        rewardRate = newRate;
    }
}

contracts/YieldForge/YieldForgeVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title YieldForgeVault
 * @notice Main vault contract for YieldForge yield optimization
 * @dev Manages user deposits, yield strategies, and automated rebalancing
 */
contract YieldForgeVault is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Vault state
    struct UserDeposit {
        uint256 amount;
        uint256 shares;
        uint256 depositTime;
        uint256 lastHarvestTime;
    }

    struct Strategy {
        address strategyAddress;
        uint256 allocation; // Percentage in basis points (10000 = 100%)
        bool active;
        uint256 totalDeposited;
    }

    // State variables
    IERC20 public immutable depositToken;
    mapping(address => UserDeposit) public userDeposits;
    mapping(uint256 => Strategy) public strategies;
    uint256 public strategyCount;
    uint256 public totalShares;
    uint256 public totalAssets;
    uint256 public performanceFee = 50; // 0.5% in basis points
    address public feeRecipient;

    // Events
    event Deposit(address indexed user, uint256 amount, uint256 shares);
    event Withdraw(address indexed user, uint256 amount, uint256 shares);
    event StrategyAdded(uint256 indexed strategyId, address strategyAddress, uint256 allocation);
    event StrategyUpdated(uint256 indexed strategyId, uint256 newAllocation);
    event Harvest(address indexed user, uint256 yield);
    event PerformanceFeeUpdated(uint256 newFee);

    constructor(
        address _depositToken,
        address _feeRecipient
    ) Ownable(msg.sender) {
        require(_depositToken != address(0), "Invalid token");
        require(_feeRecipient != address(0), "Invalid fee recipient");
        
        depositToken = IERC20(_depositToken);
        feeRecipient = _feeRecipient;
    }

    /**
     * @notice Deposit tokens into the vault
     * @param amount Amount of tokens to deposit
     */
    function deposit(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be > 0");

        // Calculate shares
        uint256 shares = totalShares == 0 
            ? amount 
            : (amount * totalShares) / totalAssets;

        // Update user deposit
        UserDeposit storage userDeposit = userDeposits[msg.sender];
        userDeposit.amount += amount;
        userDeposit.shares += shares;
        userDeposit.depositTime = block.timestamp;
        userDeposit.lastHarvestTime = block.timestamp;

        // Update totals
        totalShares += shares;
        totalAssets += amount;

        // Transfer tokens
        depositToken.safeTransferFrom(msg.sender, address(this), amount);

        emit Deposit(msg.sender, amount, shares);
    }

    /**
     * @notice Withdraw tokens from the vault
     * @param shares Amount of shares to withdraw
     */
    function withdraw(uint256 shares) external nonReentrant {
        UserDeposit storage userDeposit = userDeposits[msg.sender];
        require(shares > 0 && shares <= userDeposit.shares, "Invalid shares");

        // Calculate withdrawal amount
        uint256 amount = (shares * totalAssets) / totalShares;

        // Update user deposit
        userDeposit.amount -= amount;
        userDeposit.shares -= shares;

        // Update totals
        totalShares -= shares;
        totalAssets -= amount;

        // Transfer tokens
        depositToken.safeTransfer(msg.sender, amount);

        emit Withdraw(msg.sender, amount, shares);
    }

    /**
     * @notice Add a new yield strategy
     * @param strategyAddress Address of the strategy contract
     * @param allocation Allocation percentage in basis points
     */
    function addStrategy(
        address strategyAddress,
        uint256 allocation
    ) external onlyOwner {
        require(strategyAddress != address(0), "Invalid strategy");
        require(allocation <= 10000, "Allocation > 100%");

        strategies[strategyCount] = Strategy({
            strategyAddress: strategyAddress,
            allocation: allocation,
            active: true,
            totalDeposited: 0
        });

        emit StrategyAdded(strategyCount, strategyAddress, allocation);
        strategyCount++;
    }

    /**
     * @notice Update strategy allocation
     * @param strategyId ID of the strategy
     * @param newAllocation New allocation percentage
     */
    function updateStrategyAllocation(
        uint256 strategyId,
        uint256 newAllocation
    ) external onlyOwner {
        require(strategyId < strategyCount, "Invalid strategy");
        require(newAllocation <= 10000, "Allocation > 100%");

        strategies[strategyId].allocation = newAllocation;
        emit StrategyUpdated(strategyId, newAllocation);
    }

    /**
     * @notice Harvest yield for a user
     * @dev Called by AI agent to optimize yields
     */
    function harvest(address user) external nonReentrant returns (uint256) {
        UserDeposit storage userDeposit = userDeposits[user];
        require(userDeposit.amount > 0, "No deposit");

        // Calculate yield (simplified - in production, query actual strategies)
        uint256 timeSinceLastHarvest = block.timestamp - userDeposit.lastHarvestTime;
        uint256 yield = (userDeposit.amount * timeSinceLastHarvest * 12) / (365 days * 100); // ~12% APY

        if (yield > 0) {
            // Deduct performance fee
            uint256 fee = (yield * performanceFee) / 10000;
            uint256 userYield = yield - fee;

            // Update user deposit
            userDeposit.amount += userYield;
            userDeposit.lastHarvestTime = block.timestamp;

            // Update total assets
            totalAssets += userYield;

            // Transfer fee
            if (fee > 0) {
                depositToken.safeTransfer(feeRecipient, fee);
            }

            emit Harvest(user, userYield);
            return userYield;
        }

        return 0;
    }

    /**
     * @notice Get user's current balance including yield
     */
    function getUserBalance(address user) external view returns (uint256) {
        UserDeposit memory userDeposit = userDeposits[user];
        if (userDeposit.shares == 0) return 0;
        
        return (userDeposit.shares * totalAssets) / totalShares;
    }

    /**
     * @notice Update performance fee
     */
    function setPerformanceFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "Fee too high"); // Max 10%
        performanceFee = newFee;
        emit PerformanceFeeUpdated(newFee);
    }

    /**
     * @notice Pause deposits (emergency)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause deposits
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Get vault statistics
     */
    function getVaultStats() external view returns (
        uint256 _totalAssets,
        uint256 _totalShares,
        uint256 _strategyCount,
        uint256 _performanceFee
    ) {
        return (totalAssets, totalShares, strategyCount, performanceFee);
    }
}

